package Ejercicio7;

public class Ejercicio7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=23, b=56, aux;
		System.out.println("Valor de a y b antes del intercambio");
		System.out.println("a: "+a+" b: "+b);
		aux=a;
		a=b;
		b=aux;
		System.out.println("Valor de a y b despues del intercambio");
		System.out.println("a: "+a+" b: "+b);
	}

}
